void setup()
{
  irsend.begin();
#if ESP8266
  Serial.begin(115200, SERIAL_8N1, SERIAL_TX_ONLY);
#else  // ESP8266
  Serial.begin(115200, SERIAL_8N1);
#endif // ESP8266
}

void loop()
{
  Serial.println("allumer/eteindre");
  // irsend.sendRaw(io, 231, 38);
  irsend.sendRaw(io, 231, 38);
  delay(2000);
}
